import React from "react";
import './App.css';
import NavBar from './NavBar';
import Home from './Home';
import Memberships from './Memberships';
import Schedule from './Schedule';
import AboutUs from './AboutUs'
import Login from './Login'
// import NotFound from './NotFound';
import { BrowserRouter, Route} from "react-router-dom";
import { Routes } from 'react-router-dom'
import { useState, useEffect } from "react";
import Logout from "./Logout";
import SignUp from  "./SignUp"


const ProductDisplay = () => (
  <section>
    <div className="product">
      <img
        src="https://i.imgur.com/EHyR2nP.png"
        alt="The cover of Stubborn Attachments"
      />
      <div className="description">
      <h3>Stubborn Attachments</h3>
      <h5>$20.00</h5>
      </div>
    </div>
    <form action="/create-checkout-session" method="POST">
      <button type="submit">
        Checkout
      </button>
    </form>
  </section>
);

const Message = ({ message }) => (
  <section>
    <p>{message}</p>
  </section>
);





function App() {

  const [message, setMessage] = useState("");

  useEffect(() => {
    // Check to see if this is a redirect back from Checkout
    const query = new URLSearchParams(window.location.search);

    if (query.get("success")) {
      setMessage("Order placed! You will receive an email confirmation.");
    }

    if (query.get("canceled")) {
      setMessage(
        "Order canceled -- continue to shop around and checkout when you're ready."
      );
    }
  }, []);



  const [user, setUser] = useState(null);
  const [signUp, setSignUp] = useState(false)
  const handleSignupClick=() =>{
        setSignUp(!signUp)
    }


  useEffect(() => {
    fetch("/check_session").then((response) => {
      if (response.ok) {
        response.json().then((user) => setUser(user));
      }
    });
  }, []);

  function handleLogin(user) {
    setUser(user);
}
  function handleLogout(){
    setUser(null);
  }







  return (


    <div className = "navbar">
      {message ? <Message message={message} /> : <ProductDisplay />}
    <BrowserRouter>
    {user ? <Logout user={user} onLogout={handleLogout} />  :<Login onLogin={handleLogin} />}



      <NavBar user={user}/>

        <Routes>
          <Route path="/memberships" element={<Memberships user={user}/>}/>
          <Route path="/schedule" element={<Schedule />}/>
          <Route path="/" element={<Home />}/>
          <Route path="/aboutUs" element={<AboutUs/>}/>
          <Route path="/signup" element={<SignUp handleSignupClick = {handleSignupClick} onLogin={handleLogin}/>}/>

        </Routes>
    </BrowserRouter>


    </div>


  );
}

export default App;
